import axios from "axios";
import Layout from "../components/Layout";
import React from "react";
import Card from "@material-ui/core/Card";
import CardMedia from "@material-ui/core/CardMedia";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Container from "@material-ui/core/Container";
import { ButtonBase } from "@material-ui/core";
import Modal from '@material-ui/core/Modal';

const cardGridStyle = {
  paddingTop: "64px",
  paddingBottom: "64px",
};

const cardStyle = {
  height: "100%",
  display: "flex",
  flexDirection: "column",
};

const cardMediaStyle = {
  paddingTop: "100%", // 16:9
};

const modalStyel = {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
}

const Store = (props) => {
  return (
    <div>
      <Layout>
        {/* <h1>STORE PAGE</h1>
            {props.stores.map((store, index) => (
                <a key={index}>{store.name} / </a>
            ))} */}
        <Container maxWidth="sm">
          <Typography
            component="h1"
            variant="h2"
            align="center"
            color="textPrimary"
            gutterBottom
          >
            Store
          </Typography>
        </Container>
        <Container style={cardGridStyle} maxWidth="md">
          <Grid container spacing={4}>
            {props.stores.map((card) => (
              <Grid item key={card} xs={6} sm={4} md={3}>
                {/* <ButtonBase style={{ width: "100%" }}> */}

                <Card style={cardStyle}>
                  <CardMedia
                    style={cardMediaStyle}
                    image={card.thumb}
                    title={card.name}
                  />
                </Card>
                {/* <img src={card.thumb} />
                </ButtonBase> */}
              </Grid>
            ))}
          </Grid>
        </Container>
        <Modal open={true}>
            <div style={modalStyel}>content</div>
        </Modal>
      </Layout>
    </div>
  );
};

Store.getInitialProps = async function () {
  const response = await axios.get("http://localhost:9000/stores");
  return {
    stores: response.data,
  };
};

export default Store;
