import React from "react";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import Close from "@material-ui/icons/Close";

import { Row, Column, Item } from "@mui-treasury/components/flex";
// import { useSizedIconButtonStyles } from "@mui-treasury/styles/iconButton/sized";
import { CardContent } from "@material-ui/core";

// const CardHeader = (props) => {
//   // const iconBtnStyles = useSizedIconButtonStyles({ padding: 8, childSize: 20 });
//   return (
//     <Row {...props}>
//       <Item position={"middle"}>
//         <Typography gutterBottom variant="h5" component="h2">
//           벼락닭꼬치
//         </Typography>
//       </Item>
//       <Item position={"right"} mr={-0.5}>
//         <IconButton classes={iconBtnStyles}>
//           <Close />
//         </IconButton>
//       </Item>
//     </Row>
//   );
// };

const useStyles = makeStyles(() => ({
  card: {
    border: "2px solid",
    borderColor: "#E7EDF3",
    borderRadius: 16,
    transition: "0.4s",
    backgroundColor: "#fff",
    marginTop: 100,
  },
}));

const Popup = (props) => {
  const styles = useStyles();
  const des =
    "FOOD TRUCK - GRILLED CHICKEN\n벼락닭꼬치  CHICKEN SKEWERS\n입안에서 터지는 맛! 식도 타고 흐르는 맛!\n\n통인시장에서 줄 서서 먹는다는 효자동 닭꼬치가 커먼그라운드에 새로운 이름으로 찾아왔습니다.\n모짜렐라치즈, 소금구이 등 다양한 맛을 내는 닭꼬치가 준비되어 있습니다.\n\n상상 이상의 사이즈에 맛도, 가격도 상상 이상인 한번 맛보면 바로 단골 손님이 되는 그 맛, <벼락닭꼬치>를 푸드트럭에서 시원한 맥주와 함께 즐길 수 있습니다.\n\nLAST ORDER : 21시 30분";
  const buttonClick = () => {
    console.log("buttonClick")
  }
  
    return (
    <Grid container spacing={4} justify={"center"}>
      <Grid item xs={12} sm={8} lg={7}>
        <Row className={styles.card} p={{ xs: 0.5, sm: 0.75, lg: 1 }}>
          <Item grow>
            <img src="https://s3.ap-northeast-2.amazonaws.com/images.brandsites/common-ground/uploads/00000000354.png" style={{margin: "16px"}} />
          </Item>
          <Column>
            <CardHeader style={{margin: "16px 16px 0 16px"}}>
              <Item position={"middle"}>
                <Typography gutterBottom variant="h5" component="h2" >
                  벼락닭꼬치
                </Typography>
              </Item>
              <Item position={"right"} mr={-0.5}>
                <IconButton disabled>
                  <Close />
                </IconButton>
              </Item>
            </CardHeader>
            <CardContent>
              <Typography variant="body2" color="textSecondary" component="p">
                <span style={{ whiteSpace: "pre-line" }}>{des}</span>
              </Typography>
            </CardContent>
          </Column>
        </Row>
      </Grid>
    </Grid>
  );
};

export default Popup;
