import { makeStyles, withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Tooltip from '@material-ui/core/Tooltip';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Close from '@material-ui/icons/Close';

import { Row, Column, Item } from '@mui-treasury/components/flex';
  
  
  const useCardHeaderStyles = makeStyles(() => ({
    root: { paddingBottom: 0 },
    title: {
      fontSize: '1.25rem',
      color: '#122740',
    },
    subheader: {
      fontSize: '0.875rem',
      color: '#495869',
    },
  }));
  
  const CardHeader = props => {
    const styles = useCardHeaderStyles();
    return (
      <Row {...props}>
        <Item position={'middle'}>
          <Typography className={styles.title}>
            <b>Firebase</b>
          </Typography>
          <Typography className={styles.subheader}>
            Similar to firebase theme
          </Typography>
        </Item>
        <Item position={'right'} mr={-0.5}>
            <IconButton style={{ padding: 8, childSize: 20}}>
              <Close />
            </IconButton>
        </Item>
      </Row>
    );
  };
  
  const useStyles = makeStyles(() => ({
    card: {
      border: '2px solid',
      borderColor: '#E7EDF3',
      borderRadius: 16,
      transition: '0.4s',
      '&:hover': {
        borderColor: '#5B9FED',
      },
      backgroundColor: '#fff'
    },
  }));

const Popup = () => {
    const styles = useStyles();
  const gap = { xs: 1, sm: 1.5, lg: 2 }
    return (
        <Grid container spacing={4} justify={'center'}>
      <Grid item xs={12} sm={8} lg={7}>
        <Row className={styles.card} p={{ xs: 0.5, sm: 0.75, lg: 1 }} gap={gap}>
          <Item grow>
            <Box minHeight={200} bgcolor={'#F4F7FA'} borderRadius={8}>
                <img src="https://s3.ap-northeast-2.amazonaws.com/images.brandsites/common-ground/uploads/00000000354.png" />
            </Box>
          </Item>
          <Column>
            <CardHeader />
          </Column>
        </Row>
      </Grid>
    </Grid>
    )
}

export default Popup;
