import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Collapse from "@material-ui/core/Collapse";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import { red } from "@material-ui/core/colors";
import Close from "@material-ui/icons/Close";
import ShareIcon from "@material-ui/icons/Share";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import MoreVertIcon from "@material-ui/icons/MoreVert";

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,
    margin: theme.spacing(10),
    display: 'flex'
  },
  media: {
    height: 0,
    width: '151',
    // paddingTop: "100%",
    float:"left",
    dispaly: 'flex'
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },
  avatar: {
    backgroundColor: red[500],
  },
}));

const Popup = () => {
  const classes = useStyles();
  const description =
    "FOOD TRUCK - GRILLED CHICKEN\n벼락닭꼬치  CHICKEN SKEWERS\n입안에서 터지는 맛! 식도 타고 흐르는 맛!\n\n통인시장에서 줄 서서 먹는다는 효자동 닭꼬치가 커먼그라운드에 새로운 이름으로 찾아왔습니다.\n모짜렐라치즈, 소금구이 등 다양한 맛을 내는 닭꼬치가 준비되어 있습니다.\n\n상상 이상의 사이즈에 맛도, 가격도 상상 이상인 한번 맛보면 바로 단골 손님이 되는 그 맛, <벼락닭꼬치>를 푸드트럭에서 시원한 맥주와 함께 즐길 수 있습니다.\n\nLAST ORDER : 21시 30분";
  const img = 'https://s3.ap-northeast-2.amazonaws.com/images.brandsites/common-ground/uploads/00000000354.png'

  return (
    <Card className={classes.root}>
      <div className={classes.details}>
        <CardContent className={classes.content}>
          <Typography component="h5" variant="h5">
            Live From Space
          </Typography>
          <Typography variant="subtitle1" color="textSecondary">
            Mac Miller
          </Typography>
        </CardContent>
        <div className={classes.controls}>
          <IconButton aria-label="previous">
            {theme.direction === 'rtl' ? <SkipNextIcon /> : <SkipPreviousIcon />}
          </IconButton>
          <IconButton aria-label="play/pause">
            <PlayArrowIcon className={classes.playIcon} />
          </IconButton>
          <IconButton aria-label="next">
            {theme.direction === 'rtl' ? <SkipPreviousIcon /> : <SkipNextIcon />}
          </IconButton>
        </div>
      </div>
      <CardMedia
        className={classes.cover}
        image="/static/images/cards/live-from-space.jpg"
        title="Live from space album cover"
      />
    </Card>
  );
};

export default Popup;
